/*
oxxxx
xoxxx
xxoxx
xxxox
xxxxo
 */
public class Pattern86 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 5; i++)
		{
			for (int j = 0; j <5; j++)
			{
			if(i==j)
			{
				System.out.print("o");
			}
			else
			{
				System.out.print("x");
			}
			}
		System.out.println();	
		}
	}

}
